.election-map {
  width: 100%;
  margin: 16px 0;
  position: relative;
}

.election-map .btn-close {
  display: inline-block;
  background-image: url('data:image/svg+xml;base64,');
  background-repeat: no-repeat;
  background-position: center;
  background-size: 1rem;
  background-color: black;
